# 确保static/images目录存在
$imagesDir = "static\images"
if (-not (Test-Path $imagesDir)) {
    New-Item -ItemType Directory -Path $imagesDir -Force | Out-Null
}

# 图标的Base64编码字符串
$iconData = @{
    # 灰色图标（未激活）
    "home.png" = ""
    "stress.png" = ""
    "history.png" = ""
    
    # 紫色图标（激活状态）
    "home-active.png" = ""
    "stress-active.png" = ""
    "history-active.png" = ""
}

# 创建图标文件
$count = 0
foreach ($icon in $iconData.GetEnumerator()) {
    $filePath = Join-Path -Path $imagesDir -ChildPath $icon.Key
    $iconBytes = [Convert]::FromBase64String($icon.Value)
    [System.IO.File]::WriteAllBytes($filePath, $iconBytes)
    
    Write-Host "已创建图标: $filePath"
    $count++
}

Write-Host "完成！成功创建了 $count 个图标文件。"

# 社区图标的Base64编码数据
$communityIconBase64 = ''

$communityActiveIconBase64 = ''

try {
    # 创建目标目录
    $targetDir = Join-Path $PWD "static\images"
    if (-not (Test-Path $targetDir)) {
        New-Item -ItemType Directory -Path $targetDir -Force | Out-Null
        Write-Host "创建目录: $targetDir"
    }

    # 创建社区图标文件
    $communityIconPath = Join-Path $targetDir "community.png"
    [System.Convert]::FromBase64String($communityIconBase64) | Set-Content -Path $communityIconPath -Encoding Byte
    Write-Host "已创建图标: community.png"

    # 创建社区激活图标文件
    $communityActiveIconPath = Join-Path $targetDir "community-active.png"
    [System.Convert]::FromBase64String($communityActiveIconBase64) | Set-Content -Path $communityActiveIconPath -Encoding Byte
    Write-Host "已创建图标: community-active.png"

    Write-Host "社区图标创建完成，请刷新小程序模拟器查看效果。"
}
catch {
    Write-Host "创建图标时出错: $_"
} 